import React,{useState,useEffect} from "react";

function Form(){
    const [bookingCode, setBookingCode] = useState("");
    const [bookings,setBookings] = useState([]);

    useEffect(()=>{
        fetch(`https://bv-online-assessment.herokuapp.com/api/bookings/$`)
        .then(response => response.json())
        .then(json => setBookings(json))
    })

    function makeid(length) {
        var result           = '';
        var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
        var charactersLength = characters.length;
        for ( var i = 0; i < length; i++ ) {
            result += characters.charAt(Math.floor(Math.random() * 
            charactersLength));
        }
        return result;
    }

    function handleChange(event){
       
        console.log(event.target.value);
        setBookings(event.target.value);
        
    }

    return(
        <div>
            <h3>Your booking code</h3>
            <input type="text" placeholder={makeid(10)}
            onChange={handleChange}
            value={bookings}
            />
            <br/>
            <p>{bookings}</p>\
            {bookings.map(booking => {
                return <pre>{JSON.stringify(booking)}</pre>
            })}
        </div>
    )
}

export default Form
